fn santa(path: &str) -> i32 {
    let mut level: i32 = 0;
    let mut pos: i32 = 0;
    for c in path.chars() {
        pos += 1;
        match c {
            '(' => level += 1,
            ')' => level -= 1,
            _ => {}
        }
        if level == -1 {
            return pos;
        }
    }
    0
}

fn santa1(path: &str) -> i32 {
    path.chars()
        .filter_map(|f| match f {
            '(' => Some(1),
            ')' => Some(-1),
            _ => None,
        })
        .sum()
}

